/**
 * 目标班月结详细记录表
 * @author laiyongchang
 * */
@Entity
@Table(name = "PROMISE_CLASS_DETAIL_RECORD")
public class PromiseClassDetailRecord implements java.io.Serializable{
	
	/**
	 * 目标班详细月结ID
	 * */
	private String id;
	
	/**
	 * 目标班月结记录ID
	 * */
	private PromiseClassRecord promiseClassRecord;
	
	/**
	 * 课程类型
	 * */
	private String classType;
	
	/**
	 * 科目
	 * */
	private String subject;
	
	/**
	 * 课时 
	 * */
	private String courseHours;
	
	/**
	 * 上课老师
	 * */
	private String teacher;
	
	/**
	 * 记录创建时间
	 * */
	private String createTime;
	
	/**
	 * 记录创建者ID
	 * */
	private String createUserId;
	
	/**
	 * 记录修改时间
	 * */
	private String modifyTime;
	
	/**
	 * 记录修改人ID
	 * */
	private String modifyUserId;

	@Id
	@GenericGenerator(name = "generator", strategy = "uuid.hex")
	@GeneratedValue(generator = "generator")
	@Column(name = "ID", unique = true, nullable = false, length = 32)
	public String getId() {
		return id;
	}

	public void setId(String id) {
		this.id = id;
	}
	
	@ManyToOne(fetch = FetchType.LAZY)
	@JoinColumn(name = "PROMISE_CLASS_RECORD_ID")
	public PromiseClassRecord getPromiseClassRecord() {
		return promiseClassRecord;
	}

	public void setPromiseClassRecord(PromiseClassRecord promiseClassRecord) {
		this.promiseClassRecord = promiseClassRecord;
	}

	@Column(name = "CLASS_TYPE")
	public String getClassType() {
		return classType;
	}

	

	public void setClassType(String classType) {
		this.classType = classType;
	}

	@Column(name = "CLASS_SUBJECT")
	public String getSubject() {
		return subject;
	}

	public void setSubject(String subject) {
		this.subject = subject;
	}

	@Column(name = "COURSE_HOURS")
	public String getCourseHours() {
		return courseHours;
	}

	public void setCourseHours(String courseHours) {
		this.courseHours = courseHours;
	}

	@Column(name = "TEACHER")
	public String getTeacher() {
		return teacher;
	}

	public void setTeacher(String teacher) {
		this.teacher = teacher;
	}

	@Column(name = "CREATE_TIME")
	public String getCreateTime() {
		return createTime;
	}

	public void setCreateTime(String createTime) {
		this.createTime = createTime;
	}

	@Column(name = "CREATE_USER_ID")
	public String getCreateUserId() {
		return createUserId;
	}

	public void setCreateUserId(String createUserId) {
		this.createUserId = createUserId;
	}

	@Column(name = "MODIFY_TIME")
	public String getModifyTime() {
		return modifyTime;
	}

	public void setModifyTime(String modifyTime) {
		this.modifyTime = modifyTime;
	}

	@Column(name = "MODIFY_USER_ID")
	public String getModifyUserId() {
		return modifyUserId;
	}

	public void setModifyUserId(String modifyUserId) {
		this.modifyUserId = modifyUserId;
	}

	@Override
	public String toString() {
		return "PromiseClassDetailRecord [classType=" + classType
				+ ", courseHours=" + courseHours + ", createTime=" + createTime
				+ ", createUserId=" + createUserId + ", id=" + id
				+ ", modifyTime=" + modifyTime + ", modifyUserId="
				+ modifyUserId + ", promiseClassRecord=" + promiseClassRecord
				+ ", subject=" + subject + ", teacher=" + teacher + "]";
	}
	
	

}
